# generated by src/gen/gen_sigs_blank.rb, to be COPIED and filled in.

class NodeSigs
  def before
    %q%
      @pars = TreeSitterFFI.parser
      json = TreeSitterFFI.parser_json
      @pars.set_language(json).should == true
      @input = "[1, null]"
      @tree = @pars.parse_string(nil, @input, @input.length)
      @root_node = @tree.root_node
      @array_node = @root_node.named_child(0)
      @number_node = @array_node.named_child(0)
    %.split("\n")
  end
  
  def sig(fn_name)
    case fn_name
    when :ts_node_type then '@number_node'
		when :ts_node_symbol then '@number_node'
		when :ts_node_start_byte then '@number_node'
		when :ts_node_start_point then '@number_node'
		when :ts_node_end_byte then '@number_node'
		when :ts_node_end_point then '@number_node'
		
# 		when :ts_node_string then 
		
		when :ts_node_is_null then '@number_node'
		when :ts_node_is_named then '@number_node'
		when :ts_node_is_missing then '@number_node'
		when :ts_node_is_extra then '@number_node'
		when :ts_node_has_changes then '@number_node'
		when :ts_node_has_error then '@number_node'
		when :ts_node_parent then '@number_node'
		
		when :ts_node_child then ['@array_node, 3',
		  ['# not sure yet whether we can use just any node args, '+
		    'so try @array_node here for now']
		  ]
# 		when :ts_node_field_name_for_child then 
		when :ts_node_child_count then '@array_node'
		when :ts_node_named_child then '@array_node, 0'
		when :ts_node_named_child_count then '@array_node'
		
# 		when :ts_node_child_by_field_name then 
# 		when :ts_node_child_by_field_id then 
		
		when :ts_node_next_sibling then '@number_node'
		when :ts_node_prev_sibling then '@number_node'
		when :ts_node_next_named_sibling then '@number_node'
		when :ts_node_prev_named_sibling then '@number_node'
		
		when :ts_node_first_child_for_byte then ['@array_node, 5',
		  ['# again array_node, nec???'],
		  %q%# array_node offset 5 should be at 'u', ie "[1, n^ull]"%
		  ]
		when :ts_node_first_named_child_for_byte then ['@array_node, 1',
		  %q%# array_node offset 1 should be at '1', ie "[^1, null]"%
		  ]
		when :ts_node_descendant_for_byte_range then ['@array_node, 2, 3',
		  %q%# array_node offset 2 should be at ',', ie "[1^, null]"%
		  ]
		when :ts_node_descendant_for_point_range then '@array_node, 
		  TreeSitterFFI::Point.new, TreeSitterFFI::Point.new'
		when :ts_node_named_descendant_for_byte_range then ['@number_node, 5, 7',
		  %q%# array_node offset 5 should be at 'u', ie "[1, n^ull]"%
		  ]
		when :ts_node_named_descendant_for_point_range then '@number_node, 
		  TreeSitterFFI::Point.new, TreeSitterFFI::Point.new'
		
		when :ts_node_edit then '@number_node, TreeSitterFFI::InputEdit.new'
		when :ts_node_eq then '@number_node, TreeSitterFFI::Node.new'
    else
      nil
    end
  end
end
